#include<bits/stdc++.h>
using namespace std;
int main()
{
    int l;
    int a[100];
    int n,k;
    while(cin>>n>>k)
    {
        l=0;
        for(int i=0; i<n; i++)
        {
            cin>>a[i];
        }
        int scr=a[k-1];
        //cout<<scr<<endl;
        for(int i=0; i<n; i++)
        {
            if(a[i]>=scr&&a[i]!=0)
            {

                l=l+1;
            }
        }
        cout<<l<<endl;
    }


    return 0;
}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               